wpcloud/button.css:
& {
	width: fit-content;
	max-height: 38px;
	display: flex;
	align-items: center;
	letter-spacing: 0.125rem;
}

& a:hover {
	text-decoration: none;
}

&:hover {
	filter: brightness(80%);
}

& .wpcloud-block-button__content {
	display: flex;
	align-items: center;
	justify-content: center;
	width: fit-content;
}

&.is-secondary:not(.is-text) {
	background: transparent;
	border: 2px solid var(--wp--preset--color--accent-primary);
}

&.is-secondary:not(.is-text):hover {
	border-color: var(--wp--preset--color--foreground-primary);
	filter: none;
}

&.wpcloud-block-button__text {
	border: none;
	background: transparent;
	color: var(--wp--preset--color--foreground-primary);
	padding: 0;
}

&.wpcloud-block-button__text .wpcloud-block-icon {
	padding-left: 4px;
}

&:not(.wpcloud-block-button__text) {
	max-height: 32px;
}

&:not(.wpcloud-block-button__text) .wpcloud-block-icon {
	padding-left: 8px;
}

& .wpcloud-block-button__label {
	cursor: pointer;
}

& a:where(:not(.wp-element-button)) {
	color: unset;
}

wpcloud/expanding-header.css:
&.wpcloud-block-expanding-section__header-wrapper {
	display: grid;
	transition: grid-template-rows 0.5s ease-out;
	grid-template-rows: 1fr;
	border-top-left-radius: 5px;
	border-top-right-radius: 5px;
}

&.wpcloud-block-expanding-section__header-wrapper.has-bottom-radius {
	border-bottom-left-radius: 5px;
	border-bottom-right-radius: 5px;
}

&.wpcloud-block-expanding-section__header-wrapper.hide-on-open.section-toggled {
	  	grid-template-rows: 0fr;
}

&.wpcloud-block-expanding-section__header-wrapper .wpcloud-block-expanding-section__header {
	overflow: hidden;
 	}
&.hide-on-open .wpcloud-block-expanding-section__header{
	padding: 0;
}
& .wpcloud-block-expanding-section__header {
	padding: var(--wp--preset--spacing--30) var(--wp--preset--spacing--50);
	color: var(--wp--preset--color--foreground-secondary);
	display: flex;
	align-items: center;
	gap: 12px;
}
& .wpcloud-block-expanding-section__header .wp-block-heading {
	flex: 2;
}
& .wpcloud-block-expanding-section__header .wp-block-heading {
	margin: 8px 0;
	text-transform: none;
	font-weight: normal;
}
& .wpcloud_expanding_section_header_chevron {
	transition: transform 0.5s ease-in-out;
}
&.section-toggled .wpcloud_expanding_section_header_chevron {
	transform: rotate(90deg);
}

wpcloud/expanding-content.css:
&.wpcloud-block-expanding-section__content-wrapper {
  	display: grid;
  	grid-template-rows: 0fr;
  	transition: all 0.5s ease-out;
}

&.wpcloud-block-expanding-section__content-wrapper.section-toggled {
	grid-template-rows: 1fr;
}

&.wpcloud-block-expanding-section__content-wrapper .wpcloud-block-expanding-section__content {
	overflow: hidden;
