wpcloud/site-card.css:
& {
	display: grid;
	grid-template-columns: 40px 1fr;
	grid-template-rows: repeat(2, 20px);
	grid-column-gap: 12px;
	margin: 7px 0;


}

& img {
	grid-area: 1 / 1 / 3 / 2;
	border-radius: 4px;
}

& h2 {
	line-height: 0;
}

& h3 {
	line-height: 0;
}

& h2 {
	margin: 0;
	padding: 0;
	grid-area: 1 / 2 / 2 / 3;
}

& h3 {
	margin: 0;
	padding: 0;
	grid-area: 2 / 2 / 3 / 3;
}

& .site-title {
	margin-top: 5px;
}

& .site-title a {
	font-size: var(--wp--preset--font-size--body-medium);
	color: var(--wp--preset--color--foreground-primary);
	line-height: 10px;
	font-weight: 400;
}

& .site-title a:hover {
	filter: brightness(80%);
	text-decoration: none;
}

& .site-url {
	margin-top: 5px;
}

& .site-url a {
	font-size: var(--wp--preset--font-size--body-small);
	color: var(--wp--preset--color--foreground-secondary);
	font-weight: 400;
	line-height: 10px;
}

& .site-url a:hover {
	color: var(--wp--preset--color--accent-primary);
	text-decoration: none;
}

&.wpcloud-site-card--primary {

	grid-template-columns: 56px 1fr;
	grid-template-rows: repeat(2, 28px);
}

&.wpcloud-site-card--primary h2 {
	align-self: center;
}

&.wpcloud-site-card--primary img {
	width: 56px;
}

&.wpcloud-site-card--primary .site-title a {
	font-size: var(--wp--preset--font-size--title-medium);
	font-weight: 500;
}

&.wpcloud-site-card--primary .site-url a {
	font-size: var(--wp--preset--font-size--body-medium);
}

